import React, {
	Component
} from 'react';
import {
	Link
} from 'react-router-dom';

class Navigation extends Component {
	constructor(props) {
		super(props);
		this.state = {
			isVisible: false
		};
	}

	handleVisible = () => {
		this.setState({
			isVisible: !this.state.isVisible
		});
	};

	routeChange = () => {
		window.location.href = 'portfolio';
	};

	render() {
		return ( <
			>
			<
			div className = {
				this.state.isVisible ? 'menu' : 'menu d-none'
			} >
			<
			Link to = "/about-me"
			className = "menu-left" >
			O mnie <
			/Link> <
			div className = "menu-center" >
			Portfolio <
			div className = "menu-center-more" >
			<
			Link to = "/portfolio/aplikacje-mobilne" > Aplikacje mobilne < /Link> <
			Link to = "/portfolio/strony-internetowe" > Strony internetowe < /Link> <
			Link to = "/portfolio/projekty-graficzne" >
			Pozosta≈Çe projekty graficzne <
			/Link> <
			Link to = "portfolio/elementy-do-druku" > Elementy do druku < /Link> <
				/div> <
				/div>

				<
				Link to = "/contact"
			className = "menu-right" >
			Kontakt <
			/Link> <
			/div>

			<
			button onClick = {
				this.handleVisible
			}
			className = {
				`hamburger hamburger--vortex ${
						this.state.isVisible ? 'is-active' : null
					}`
			}
			type = "button" >
			<
			span className = "hamburger-box" >
			<
			span className = "hamburger-inner" > < /span> <
			/span> <
			/button> <
			/>
		);
	}
}

export default Navigation;
