import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import ToggleButton from './ToggleButton/ToggleButton';

class Navigation extends Component {
	constructor(props) {
		super(props);
		this.state = {
			isVisible: true
		};
	}

	render() {
		return (
			<>
				<div className={this.state.isVisible ? 'menu d-none' : 'menu d-none'}>
					<Link to="/about-me" className="menu-left">
						O mnie
					</Link>
					<Link to="/portfolio" className="menu-center">
						Portfolio
					</Link>
					<Link to="/contact" className="menu-right">
						Kontakt
					</Link>
				</div>

				<ToggleButton onClick={this.handleIsVisible} />
			</>
		);
	}
}

export default Navigation;
