import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import ToggleButton from './ToggleButton/ToggleButton';

class Navigation extends Component {
	constructor(props) {
		super(props);
		this.state = {
			isVisible: true
		};
	}

	handleIsVisible = () => {
		this.setState({
			isVisible: false
		});
	};

	render() {
		return (
			<>
				<div
					onClick={this.handleIsVisible}
					className={this.state.isVisible ? 'menu' : 'menu d-none'}
				>
					<Link to="/about-me" className="menu-left">
						O mnie
					</Link>
					<Link to="/portfolio" className="menu-center">
						Portfolio
					</Link>
					<Link to="/contact" className="menu-right">
						Kontakt
					</Link>
				</div>

				<ToggleButton onClick={this.handleIsVisible} />
			</>
		);
	}
}

export default Navigation;
